function App() {
  const [isEditAvatarPopupOpened, setIsEditAvatarPopupOpened] = useState(false);
  const [isEditProfilePopupOpened, setIsEditProfilePopupOpened] = useState(false);
  const [isAddCardPopupOpened, setIsAddCardPopupOpened] = useState(false);

  isEditAvatarPopupOpened = isOpened;
  isEditProfilePopupOpened = isOpened;
  isAddCardPopupOpened = isOpened;


  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpened(true);
  };
  function handleEditProfileClick() {
    setIsEditProfilePopupOpened(true);
  };
  function handleAddCardClick() {
    setIsAddCardPopupOpened(true);
  };
  return (
    <div className="page">
      <div className="page-container">
        <Header />
        <Main 
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onAddCard={handleAddCardClick}/>
        <Footer />
      </div>
    </div>
  );
}

export default App;
