import React, { useEffect } from 'react';
import {Link} from 'react-router-dom';
import logo from '../images/__logo.svg';

function Header (props) {

  const signUpBtnData = {
    handler: function () {},
    hystoryPath: '/signup',
    text: 'Регистрация'
  }
  const signInBtnData = {
    handler: function () {},
    hystoryPath: '/signin',
    text: 'Войти'
  }
  const signedBtnData = {
    handler: function () {
    localStorage.removeItem('token')
    },
    hystoryPath: '/signin',
    text: 'Выйти'
  }

  useEffect( () => {
    if(props.hystory.location.pathname == '/') {
      props.setHeaderBtnData(signedBtnData)
    }
    if(props.hystory.location.pathname == '/sign-in') {
      props.setHeaderBtnData(signUpBtnData)
    }
    if(props.hystory.location.pathname == '/sign-up') {
      props.setHeaderBtnData(signInBtnData)
    }
  }, [])


  return (
    <header className="header">
      <img 
        className="header__logo" 
        src={logo} 
        alt="логотип mesto russia" 
      /> 
    </header>
  );
}

export default Header;
