import React from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm';

function EditAvatarPopup (props) {
  const [avatar, setAvatar] = React.useState("");
  const avatarRef = React.useRef();

  function handleSubmit(e) {
    e.preventDefault();

    props.onUpdateAvatar({
      avatar: avatarRef.current.value()
     });
  }

  function handleAvatarChange(e) {
    setAvatar(e.target.avatar);
  }

  return (
    <PopupWithForm
      name='edit-avatar'
      title='Обновить аватар'
      buttonText='Сохранить'
      isOpen={props.isOpen}
      onClose={props.onClose}
      onSubmit={handleSubmit}
    >
    <section className="form__section">
    <input ref={avatarRef} className="form__input" type="url" id="form__input-avatar-link" 
        name="link" placeholder="Ссылка" minLength="0" required onChange={handleAvatarChange} />
    <span className="form__input-error" id="form__input-avatar-link-error"></span>
    </section>
    </PopupWithForm>
  );
}

export default EditAvatarPopup;
