import React from 'react';
import { Route } from 'react-router-dom';
import api from './../../utils/Api.js';
import Header from './Header/Header.js';
import Main from './Main/Main.js';
import Footer from './Footer/Footer.js';
import PopupWithForm from './PopupWithForm/PopupWithForm.js';
import ImagePopup from './ImagePopup/ImagePopup.js';
import CurrentUserContext from './../contexts/CurrentUserContext.js';

function App() {
  const [isEditAvatarPopupOpened, setIsEditAvatarPopupOpened] = React.useState(false);
  const [isEditProfilePopupOpened, setIsEditProfilePopupOpened] = React.useState(false);
  const [isAddCardPopupOpened, setIsAddCardPopupOpened] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [сurrentUser, setCurrentUser] = React.useState();

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpened(true);
  };

  function handleEditProfileClick() {
    setIsEditProfilePopupOpened(true);
  };
  function handleAddCardClick() {
    setIsAddCardPopupOpened(true);
  };

  function closeAllPopups () {
    setIsEditAvatarPopupOpened(false);
    setIsEditProfilePopupOpened(false);
    setIsAddCardPopupOpened(false);
    setSelectedCard({});
  }

  const handleCardClick = (card) => {
    setSelectedCard(card)
  }

  React.useEffect(() => {
    api.getUserInfo()
      .then(([userData]) => {
        setCurrentUser(userData);

      })
      .catch((error) => console.log(error));
  }, [])

  return (
    <div className="page">
      <div className="page-container">
        <Header />
        <Main 
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onAddCard={handleAddCardClick}
          onCardClick={handleCardClick}
        />
        <Footer />
        <PopupWithForm
          name='edit-avatar'
          title='Обновить аватар'
          isOpen={isEditAvatarPopupOpened}
          buttonText='Сохранить'
          onClose={closeAllPopups}
        >
          <section className="form__section">
            <input className="form__input" type="url" id="form__input-avatar-link" 
               name="link" placeholder="Ссылка" minLength="0" required />
            <span className="form__input-error" id="form__input-avatar-link-error"></span>
          </section>
        </PopupWithForm>
        <PopupWithForm
          name='edit-profile'
          title='Редактировать профиль'
          isOpen={isEditProfilePopupOpened}
          buttonText='Сохранить'
          onClose={closeAllPopups}
        >
          <section className="form__section">
            <input className="form__input" id="form__input-name" name="nameInput" 
              type="text" placeholder="Имя" minLength="2" maxLength="40" required/>
            <span className="form__input-error" id="form__input-name-error"></span>
          </section> 
          <section className="form__section">
            <input className="form__input" id="form__input-profession" name="professionInput"
              type="text"  placeholder="Профессия" minLength="2" maxLength="200" required/>
            <span className="form__input-error" id="form__input-profession-error"></span>
          </section>
        </PopupWithForm>
        <PopupWithForm
          name='add-card'
          title='Новое место'
          isOpen={isAddCardPopupOpened}
          buttonText='Создать'
          onClose={closeAllPopups}
        >
        <section className="form__section">
          <input className="form__input" type="text" id="form__input-place-title" name="name" 
            placeholder="Название" minLength="1" maxLength="30" required />
          <span className="form__input-error" id="form__input-place-title-error"></span>
        </section>
        <section className="form__section">
          <input className="form__input" type="url" id="form__input-place-link" name="link" 
            placeholder="Ссылка" minLength="0" required />
          <span className="form__input-error" id="form__input-place-link-error"></span>
        </section>
        </PopupWithForm>
        <ImagePopup
          name='preview-card'
          card={selectedCard}
          onClose={closeAllPopups}
        />
        {/* <Route path="/">
          < />
        </Route> */}
      </div>
    </div>
  );
}

export default App;
