import React from "react";
import auth_success from '../images/auth-success.svg';
import auth_fail from '../images/auth-fail.svg';

function InfoTooltip (props) {

  return (
    <section className=
      {`popup popup-info-tooltip
      ${props.isOpen ? 'popup_opened' : ''}`
      } 
    >
      <div className="popup__content">
        <button 
          className={`popup__close-button opacity-transition`} 
          type="button" 
          aria-label="Закрыть"
          onClick={props.onClose}
        />
        <img 
          src={`${props.registed
            ? auth_success
            : auth_fail
          }`}
          alt={props.title} 
        />
        <h2 className="popup__title">{`${props.registed
            ? 'Вы успешно зарегистрировались!' 
            : 'Что-то пошло не так! Попробуйте ещё раз.'
          }`}
        </h2>
      </div>
    </section>
  );
}

export default InfoTooltip;
