import React from 'react';

function PopupWithForm (props) {
  return (
    <section className={`popup popup_type_${props.name}`} >
      <div className="popup__content">
        <button 
          className={`popup__close-button popup-${props.name}__close-button opacity-transition`} 
          type="button" 
          aria-label="Закрыть">
        </button>
        <h2 className="popup__title">{props.title}</h2>
        <form 
          className="form" 
          action="#" 
          name={props.name}
          noValidate 
          autoComplete="off">
        {props.children}
        </form>
      </div>
    </section>

  );
}

export default PopupWithForm;
