import React from 'react';
import Header from './Header/Header.js';
import Main from './Main/Main.js';
import Footer from './Footer/Footer.js';

function App() {
  const [isEditAvatarPopupOpened, setIsEditAvatarPopupOpened] = useState(false);
  const [isEditProfilePopupOpened, setIsEditProfilePopupOpened] = useState(false);
  const [isAddCardPopupOpened, setIsAddCardPopupOpened] = useState(false);


  function handleEditAvatarClick() {
    document
      .querySelector(".popup-edit-avatar")
      .classList.add("popup_opened");
  };
  function handleEditProfileClick() {
    document
      .querySelector(".popup-edit-profile")
      .classList.add("popup_opened");
  };
  function handleAddCardClick() {
    document
      .querySelector(".popup-add-card")
      .classList.add("popup_opened");
  };
  return (
    <div className="page">
      <div className="page-container">
        <Header />
        <Main 
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onAddCard={handleAddCardClick}/>
        <Footer />
      </div>
    </div>
  );
}

export default App;
