import React, { useEffect } from 'react';
import PopupWithForm from './PopupWithForm.js';

function AddPlacePopup ({isOpen, onClose, onAddPlace}) {
  const [name, setName] = React.useState('');
  const [link, setLink] = React.useState('');
  // const [isSubmitted, setIsSubmitted] = React.useState(false);
  const [formValues, setFormValues] = React.useState({
    placeName: '',
    placeLink: ''
  })

  function handleNameChange(e) {
    setName(e.target.value);
  }

  function handleLinkChange(e) {
    setLink(e.target.value);
  }

  function handleSubmit(e, isOpen) {
    e.preventDefault();
    onAddPlace({name, link});
    if (!isOpen) {
      setName("");
      setLink("");
    }
  }

  const {placeName, placeLink} = formValues;

  useEffect(function validateInputs() {
    const isPlaceNameFilled = formValues.placeName.length > 0;
    const isPlaceNameValid = isPlaceNameFilled;

    const isPlaceLinkFilled = formValues.placeLink.length > 0;
    const isPlaceLinkValid = isPlaceLinkFilled;
  }, [formValues]);

  return (
    <PopupWithForm
      name='add-card'
      title='Новое место'
      buttonText='Создать'
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
    >
      <section className="form__section">
        <input className="form__input" type="text" id="form__input-place-title" name="name" 
          placeholder="Название" minLength="1" maxLength="30" required value={placeName || ''} onChange={handleNameChange}/>
        <span className="form__input-error" id="form__input-place-title-error"></span>
      </section>
      <section className="form__section">
        <input className="form__input" type="url" id="form__input-place-link" name="link" 
          placeholder="Ссылка" minLength="0" required value={placeLink || ''} onChange={handleLinkChange}/>
        <span className="form__input-error" id="form__input-place-link-error"></span>
      </section>
    </PopupWithForm>
  )
}

export default AddPlacePopup;
