function PopupWithForm (props) {
  return (
    <section className={`popup popup_type_${props.name} ${props.isOpen ? 'popup_opened' : ''}`} >
      <div className="popup__content">
        <button 
          className={`popup__close-button popup-${props.name}__close-button opacity-transition`} 
          type="button" 
          aria-label="Закрыть"
          onClick ={props.onClose}>
        </button>
        <h2 className="popup__title">{props.title}</h2>
        <form 
          className="form" 
          action="#" 
          name={props.name}
          noValidate 
          autoComplete="off">
        {props.children}
          <button
            className="form__submit-button form__submit-button_type_edit-avatar opacity-transition" 
            type="submit">
            {props.buttonText}
          </button>
        </form>
      </div>
    </section>
  );
}

export default PopupWithForm;
