import React from 'react';
import { CurrentUserContext } from './../../contexts/CurrentUserContext.js';

function Card({ card, onCardClick }) {
  const isOwn = card.owner._id === currentUser._id;
  const cardDeleteButtonClassName = (
    `card__delete-button ${isOwn ? 'card__delete-button' : 'hidden'}`
  );

  const isLiked = card.likes.some(i => i._id === currentUser._id);
  const cardLikeButtonClassName = (
    `card__delete-button ${isLiked ? 'card__delete-button' : 'hidden'}`
  ); 

  function handleClick() {
    onCardClick(card);
  }  
  return (
    <article className="card">
      <button className="card__delete-button hidden opacity-transition" type="button" aria-label="Удалить"></button>
      <img className="card__image" src={card.link} alt={card.name} onClick={handleClick}/>
      <div className="card__main">
        <h2 className="card__title">{card.name}</h2>
        <div className="card__likes-container">
          <button className="card__like-button opacity-transition" type="button" aria-label="Оценить"></button>
          <span className="card__likes">{card.likes.length}</span>
        </div>
      </div>
    </article>
  )
}

export default Card;
