export const _BASE_URL = 'https://auth.nomoreparties.co';

function _checkResponse(res) {
  if (res.ok) {
    return res.json();
  }
  return Promise.reject(`Ошибка ${res.status}`);
}

function _makeRequest({ endpoint, method, body }) {
  const fetchInit = {
    method: method,
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    },
  };
  return fetch (
    `${_BASE_URL}/${endpoint}`,
    body 
      ? { ...fetchInit, body: JSON.stringify(body) } 
      : fetchInit
  )
  .then(_checkResponse)
}

export const register = (email, password) => {
  return _makeRequest({
    endpoint: 'signup', 
    method: 'POST',
    body: {
      'password': password,
      'email': email
    }
  })
};

export const authorize = (email, password) => {
  return _makeRequest({
    endpoint: 'signin', 
    method: 'POST',
    body: {
      'password': password,
      'email': email
    }
  })
  .then((data) => {
    if (data){
      localStorage.setItem('jwt', data.token);
      return data;
    } 
  })
};

export const checkToken = (token) => {
  return _makeRequest({
    endpoint: '/users/me', 
    method: 'GET'
  })
  .then(_checkResponse)
};
