import React from 'react';
import { CurrentUserContext } from './../../contexts/CurrentUserContext.js';

function Card(props) {
  const currentUser = React.useContext(CurrentUserContext);

  const isOwn = props.card.owner._id === currentUser._id;

  const cardDeleteButtonClassName = (
    `card__delete-button opacity-transition ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
  ); 

  const isLiked = props.card.likes.some(i => i._id === currentUser._id);
  const cardLikeButtonClassName = (
    `card__like-button opacity-transition ${isLiked ? 'card__like-button_active' : 'card__like-button_inactive'}`
  ); 

  function handleClick() {
    props.onCardClick(props.card);
  };

  function handleLikeClick() {
    props.onCardLike(props.card);
  };

  function handleDeleteClick() {
    props.onCardDelete(props.card);
  };

  return (
    <article className="card">
      <button className={`${cardDeleteButtonClassName}`} type="button" 
        aria-label="Удалить" onClick={handleDeleteClick}></button>
      <img className="card__image" src={props.card.link} alt={props.card.name} onClick={handleClick}/>
      <div className="card__main">
        <h2 className="card__title">{props.card.name}</h2>
        <div className="card__likes-container">
          <button className={`${cardLikeButtonClassName}`}
          type="button" aria-label="Оценить" onClick={handleLikeClick}></button>
          <span className="card__likes">{props.card.likes.length}</span>
        </div>
      </div>
    </article>
  )
}

export default Card;
