import React from 'react';
import api from './../utils/Api.js';
import Header from './Header/Header.js';
import Main from './Main/Main.js';
import PopupWithForm from './PopupWithForm/PopupWithForm.js';
import EditProfilePopup from './EditProfilePopup/EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup/EditAvatarPopup.js';
import ImagePopup from './ImagePopup/ImagePopup.js';
import Footer from './Footer/Footer.js';
import { CurrentUserContext } from './../contexts/CurrentUserContext.js';

function App() {
  const [isEditAvatarPopupOpened, setIsEditAvatarPopupOpened] = React.useState(false);
  const [isEditProfilePopupOpened, setIsEditProfilePopupOpened] = React.useState(false);
  const [isAddCardPopupOpened, setIsAddCardPopupOpened] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [currentUser, setCurrentUser] = React.useState('');

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpened(true);
  };

  function handleUpdateAvatar() {
    api.setUserAvatar();
  };

  function handleEditProfileClick() {
    setIsEditProfilePopupOpened(true);
  };

  function handleAddCardClick() {
    setIsAddCardPopupOpened(true);
  };

  const handleCardClick = (card) => {
    setSelectedCard(card)
  }

  function closeAllPopups () {
    setIsEditAvatarPopupOpened(false);
    setIsEditProfilePopupOpened(false);
    setIsAddCardPopupOpened(false);
    setSelectedCard({});
  }

  React.useEffect(() => {
    api.getUserInfo()
      .then((userData) => {
        setCurrentUser(userData);
      })
      .catch((error) => console.log(error));
  }, [])

  return (
    <div className="page">
      <div className="page-container">
        <CurrentUserContext.Provider value={currentUser}>
          <Header />
          <Main 
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onAddCard={handleAddCardClick}
            onCardClick={handleCardClick}
          />
          <EditAvatarPopup isOpen={isEditAvatarPopupOpened} onClose={closeAllPopups} /> 
          <EditProfilePopup isOpen={isEditProfilePopupOpened} onClose={closeAllPopups} />
          <PopupWithForm
            name='add-card'
            title='Новое место'
            isOpen={isAddCardPopupOpened}
            buttonText='Создать'
            onClose={closeAllPopups}
          >
            <section className="form__section">
              <input className="form__input" type="text" id="form__input-place-title" name="name" 
                placeholder="Название" minLength="1" maxLength="30" required />
              <span className="form__input-error" id="form__input-place-title-error"></span>
            </section>
            <section className="form__section">
              <input className="form__input" type="url" id="form__input-place-link" name="link" 
                placeholder="Ссылка" minLength="0" required />
                <span className="form__input-error" id="form__input-place-link-error"></span>
            </section>
          </PopupWithForm>
          <ImagePopup
            name='preview-card'
            card={selectedCard}
            onClose={closeAllPopups}
          />
          <Footer />
        </CurrentUserContext.Provider>
      </div>
    </div>
  );
}

export default App;
