import React from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm';

function EditProfilePopup ({isOpen, onClose}) {
  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");

  function handleNameChange(e) {
    setName(e.target.name);
  }

  function handleDescriptionChange(e) {
    setDescription(e.target.description);
  }

  return (
    <PopupWithForm
      name='edit-profile'
      title='Редактировать профиль'
      buttonText='Сохранить'
      isOpen={isOpen}
      onClose={onClose}
      onNameChange={handleNameChange}
      onDescriptionChange={handleDescriptionChange}
    >
      <section className="form__section">
        <input className="form__input" id="form__input-name" name="nameInput" type="text" 
          placeholder="Имя" minLength="2" maxLength="40" required />
        <span className="form__input-error" id="form__input-name-error"></span>
      </section> 
      <section className="form__section">
        <input className="form__input" id="form__input-profession" name="professionInput" type="text" 
          placeholder="Профессия" minLength="2" maxLength="200" required />
        <span className="form__input-error" id="form__input-profession-error"></span>
      </section>
    </PopupWithForm>
  );
}

export default EditProfilePopup
