import react from 'react';
import React from 'react';
import { currentUser } from '../../contexts/CurrentUserContext';
import PopupWithForm from '../PopupWithForm/PopupWithForm';

function EditProfilePopup (props) {
  const [name, setName] = React.useState(currentUser ? currentUser.name : '');
  const [description, setDescription] = React.useState(currentUser ? currentUser.about : '');
  React.useEffect(() => {
    setName(currentUser ? currentUser.name : '');
    setDescription(currentUser ? currentUser.about : '')
  }, [currentUser])

  function handleNameChange(e) {
    setName(e.target.value);
  }

  function handleDescriptionChange(e) {
    setDescription(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();

    props.onEditProfile({
      name,
      about: description,
    });
  } 

  return (
    <PopupWithForm
      name='edit-profile'
      title='Редактировать профиль'
      buttonText='Сохранить'
      isOpen={props.isOpen}
      onClose={props.onClose}
      onSubmit={handleSubmit}
    >
      <section className="form__section">
        <input className="form__input" id="form__input-name" name="nameInput" type="text" 
          placeholder="Имя" minLength="2" maxLength="40" required value={name} onChange={handleNameChange}/>
        <span className="form__input-error" id="form__input-name-error"></span>
      </section> 
      <section className="form__section">
        <input className="form__input" id="form__input-profession" name="professionInput" type="text" 
          placeholder="Профессия" minLength="2" maxLength="200" required value={description} onChange={handleDescriptionChange}/>
        <span className="form__input-error" id="form__input-profession-error"></span>
      </section>
    </PopupWithForm>
  );
}

export default EditProfilePopup
