import React from 'react';
import Header from './Header/Header.js';
import Main from './Main/Main.js';
import Footer from './Footer/Footer.js';
import PopupWithForm from './PopupWithForm/PopupWithForm.js';
// import PopupWithImage from './PopupWithForm/PopupWithForm.js';

function App() {
  const [isEditAvatarPopupOpened, setIsEditAvatarPopupOpened] = React.useState(false);
  const [isEditProfilePopupOpened, setIsEditProfilePopupOpened] = React.useState(false);
  const [isAddCardPopupOpened, setIsAddCardPopupOpened] = React.useState(false);
  const [popupContent, setPopupContent] = React.useState(null);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpened(true);
    const name = 'edit-avatar';
    const title = 'Обновить аватар';
    setPopupContent(
    `<section className="form__section">
      <input className="form__input" type="url" defaultValue id="form__input-avatar-link" 
        name="link" placeholder="Ссылка" minlength="0" required>
      <span className="form__input-error" id="form__input-avatar-link-error"></span>
    </section>
    <button class="form__submit-button form__submit-button_type_edit-avatar opacity-transition" 
      type="submit">Сохранить</button>`
    )
  };

  function handleEditProfileClick() {
    setIsEditProfilePopupOpened(true);
  };
  function handleAddCardClick() {
    setIsAddCardPopupOpened(true);
  };

  function closeAllPopups () {
    setIsEditAvatarPopupOpened(false);
    setIsEditProfilePopupOpened(false);
    setIsAddCardPopupOpened(false);
  }

  return (
    <div className="page">
      <div className="page-container">
        <Header />
        <Main 
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onAddCard={handleAddCardClick}
          onClose={closeAllPopups}
        />
        <PopupWithForm
          name={name}
          title={title}
        >
          {popupContent}
        </PopupWithForm>
        <Footer />
      </div>
    </div>
  );
}

export default App;
