import React from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm';

function EditAvatarPopup (props) {
  const avatarRef = React.useRef();

  function handleSubmit(e) {
    e.preventDefault();

    props.onUpdateAvatar(
      avatarRef.current.value()
    );
  }

  return (
    <PopupWithForm
      name='add-card'
      title='Новое место'
      isOpen={isAddCardPopupOpened}
      buttonText='Создать'
      onClose={closeAllPopups}
    >
    <section className="form__section">
    <input ref={avatar} className="form__input" type="url" id="form__input-avatar-link" 
        name="link" placeholder="Ссылка" minLength="0" required onChange={handleAvatarChange}/>
    <span className="form__input-error" id="form__input-avatar-link-error"></span>
    </section>
    </PopupWithForm>
  );
}

export default EditAvatarPopup;
