class Api {
  constructor(options) {
    this._address = options._address;
    this._token = options._token;
    this._cohortId = options._cohortId;
  }

  _handleResponse = (response) => {
    if (response.ok) {
      return response.json();
    } else {
      return Promise.reject(response.status);
    }
  }

  _makeRequest (endpoint, method, body) {
    const fetchInit = {
      method: method,  
      headers: {
        authorization: this._token,
        'Content-Type': 'application/json'
      }
    };
    return fetch (
      `${this.address}/${this._cohortId}/${endpoint}`,
      body ? { ...fetchInit, body: JSON.stringify(body) } : fetchInit)
    .then (
      this._handleResponse
    )
  }

  getUserInfo () {
    return this._makeRequest('users/me', 'GET');
  }

  getInitialCards () {
    return this._makeRequest('cards', 'GET');
  }

  updateUserInfo (userInfo) {
    return this._makeRequest('users/me', 'PATCH', userInfo);
  }

  updateAvatar (avatar) {
    return this._makeRequest('users/me/avatar', 'PATCH', avatar);
  }

  addNewCard (cardData) {
    return this._makeRequest('cards', 'POST', cardData);
  }

  likeCard (cardID, isLiked) {
    return this._makeRequest(`cards/likes/${cardID}`, isLiked ? 'DELETE' : 'PUT');
  }

  deleteCard (cardID) {
    return this._makeRequest(`cards/${cardID}`, 'DELETE');
  }
}

const api = new Api ({
  address: 'https://mesto.nomoreparties.co/v1',
  token: 'c846985c-30b9-4d91-bdf7-4d0b3c99bbf7',
  cohortId: 'cohort-24',
})

export default api;
