import React, { useEffect } from 'react';
import { Route, Switch, Link } from 'react-router-dom';
import logo from '../images/__logo.svg';

function Header (props) {

  const signUpBtnData = {
    handler: function () {},
    historyPath: 'sign-up',
    text: 'Регистрация'
  }

  const signInBtnData = {
    handler: function () {},
    historyPath: 'sign-in',
    text: 'Войти'
  }

  const signedInBtnData = {
    handler: function () {
      localStorage.removeItem('token')
    },
    historyPath: 'sign-in',
    text: 'Выйти'
  }

  useEffect(() => {
    if(props.hystory.location.pathname == '/') {
      props.setHeaderbtnData(signedInBtnData)
    }
    if(props.hystory.location.pathname == '/sign-in') {
      props.setHeaderbtnData(signUpBtnData)
    }
    if(props.hystory.location.pathname == '/') {
      props.setHeaderbtnData(signInBtnData)
    }
  })

  return (
    <header className="header">
      <img 
        className="header__logo" 
        src={logo} 
        alt="логотип mesto russia" 
      />
      <Switch>
        <Route path='sign-up'><Link to='sign-in'>Войти</Link></Route>
        <Route path='sign-in'><Link to='sign-in'>Регистрация</Link></Route>
        <Route path='/'></Route>
      </Switch>
    </header>
  );
}

export default Header;
