import React from 'react';
import { CurrentUserContext } from './../../contexts/CurrentUserContext.js';
import api from './../../utils/Api.js';
import Card from './../Card/Card.js';

function Main (props) {
  const currentUser = React.useContext(CurrentUserContext);

  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getInitialCards()
      .then((cardData) => {
        setCards(cardData);
      })
      .catch((error) => console.log(error));
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.likeCard(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  };

  function handleCardDelete(card) {
    const isLiked = cards.some(i => i._id === currentUser._id);

    api.deleteCard(card._id).then((card) => {
    });
  };

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__info-column">
          <div className="profile__avatar" style={{ backgroundImage: `url(${currentUser.avatar})` }} >
            <button 
              onClick={props.onEditAvatar}
              className="profile__avatar-edit-button opacity-transition"
              type="button"
              aria-label="Редактировать аватар">
            </button>
            </div>
            <div className="profile__info-container">
            <div className="profile__user-data-container">
              <h1 className="profile__name">{currentUser.name}</h1>
              <p className="profile__profession">{currentUser.about}</p>
            </div>
            <button 
              onClick={props.onEditProfile}
              className="profile__edit-button opacity-transition" 
              type="button" 
              aria-label="Редактировать профиль">
            </button>
            </div>
        </div>
        <button 
          onClick={props.onAddCard}
          className="profile__add-card-button opacity-transition" 
          type="button" 
          aria-label="Добавить карточку">
        </button>
      </section>
      <section className="photo-grid">
        {cards.map((card) => {
          return (<Card key={card._id} card={card} 
            onCardClick={props.onCardClick} onCardLike={handleCardLike} onCardLike={handleCardDelete}/>)
        })}
      </section>
    </main>
  );
}

export default Main
