import React from 'react';
import PopupWithForm from './PopupWithForm';

function EditAvatarPopup ({isOpen, onClose, onUpdateAvatar}) {
  const avatarRef = React.useRef();
  const [isSubmitted, setisSubmitted] = React.useState(false);
  const [buttonText, setButtonText] = React.useState(`${PopupWithForm.buttonText}`);

  function handleSubmit(e) {
    e.preventDefault();
    setisSubmitted(true);
    onUpdateAvatar({
      avatar: avatarRef.current.value
    });
  }

  function setLoadingButtonText() {
    if (isSubmitted) {
      setButtonText('Сохранение...');
    } else {
      setButtonText(buttonText);
    }
  }

  return (
    <PopupWithForm
      name='edit-avatar'
      title='Обновить аватар'
      buttonText={setLoadingButtonText}
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
    >
    <section className="form__section">
    <input ref={avatarRef} className="form__input" type="url" id="form__input-avatar-link" 
        name="link" placeholder="Ссылка" minLength="0" required />
    <span className="form__input-error" id="form__input-avatar-link-error"></span>
    </section>
    </PopupWithForm>
  );
}

export default EditAvatarPopup;
