export const _BASE_URL = 'https://auth.nomoreparties.co';

function _checkResponse(res) {
  if (res.ok) {
    return res.json();
  }
  return Promise.reject(`Ошибка ${res.status}`);
}

function _makeRequest({ endpoint, method, body }) {
  const fetchInit = {
    method: method,
    headers: {
      'Content-Type': 'application/json'
    },
  };
  return fetch (
    `${_BASE_URL}/${endpoint}`,
    body 
      ? { ...fetchInit, body: JSON.stringify({body}) } 
      : fetchInit
  )
  .then(_checkResponse)
}

export const register = () => {
  return _makeRequest({
    endpoint: 'signup', 
    method: 'POST'
  })
};

export const authorize = () => {
  return _makeRequest({
    endpoint: 'signin', 
    method: 'POST'
  })
  .then((data) => {
    if (data){
      localStorage.setItem('jwt', data.token);
      return data;
    } 
  })
};

export const checkToken = (token) => {
  return fetch(`${_BASE_URL}/users/me`, {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`,
    }
  })
  .then(_checkResponse)
};
